import React, { useState, useEffect } from 'react';
import axios from 'axios';

const CourseInstanceComponent = () => {
  const [courses, setCourses] = useState([]);
  const [year, setYear] = useState('');
  const [semester, setSemester] = useState('');
  const [courseId, setCourseId] = useState('');
  const [instance, setInstance] = useState({});

  useEffect(() => {
    axios.get('/api/courses/')
      .then(response => {
        setCourses(response.data);
      })
      .catch(error => {
        console.error(error);
      });
  }, []);

  const handleAddInstance = (event) => {
    event.preventDefault();
    const data = {
      year: year,
      semester: semester,
      course_id: courseId,
    };
    axios.post('/api/instances/', data)
      .then(response => {
        setInstance(response.data);
      })
      .catch(error => {
        console.error(error);
      });
  };

  const handleGetDetail = (event) => {
    event.preventDefault();
    axios.get(`/api/instances/${year}/${semester}/${courseId}/`)
      .then(response => {
        setInstance(response.data);
      })
      .catch(error => {
        console.error(error);
      });
  };

  return (
    <div>
      <h1>Add Course Instance</h1>
      <form onSubmit={handleAddInstance}>
        <label>Year:</label>
        <input type="number" value={year} onChange={(event) => setYear(event.target.value)} />
        <br />
        <label>Semester:</label>
        <input type="number" value={semester} onChange={(event) => setSemester(event.target.value)} />
        <br />
        <label>Course:</label>
        <select value={courseId} onChange={(event) => setCourseId(event.target.value)}>
          {courses.map((course) => (
            <option key={course.id} value={course.id}>{course.name}</option>
          ))}
        </select>
        <br />
        <button type="submit">Add Instance</button>
      </form>
      <h1>Course Instance Detail</h1>
      {instance && (
        <div>
          <p>Year: {instance.year}</p>
          <p>Semester: {instance.semester}</p>
          <p>Course: {instance.course.name}</p>
        </div>
      )}
      <button onClick={handleGetDetail}>Get Detail</button>
    </div>
  );
};

export default CourseInstanceComponent;
