import React, { useState, useEffect } from 'react';
import axios from 'axios';

const CourseInstanceList = () => {
  const [courseInstances, setCourseInstances] = useState([]);
  const [filteredInstances, setFilteredInstances] = useState([]);
  const [filterYear, setFilterYear] = useState('');
  const [filterSemester, setFilterSemester] = useState('');

  const [courses, setCourses] = useState([]); // State for courses
  const [newInstance, setNewInstance] = useState({
    course: '',
    year: '',
    semester: ''
  });

  // Fetch course instances with filter parameters
  const fetchCourseInstances = async () => {
    try {
      const response = await axios.get(
        `http://127.0.0.1:8000/api/instances/${filterYear}/${filterSemester}/`
      );
      setCourseInstances(response.data);
    } catch (error) {
      console.error("There was an error fetching the course instances!", error);
    }
  };

  // Fetch courses for the dropdown
  const fetchCourses = async () => {
    try {
      const response = await axios.get('http://127.0.0.1:8000/api/courses/');
      setCourses(response.data);
    } catch (error) {
      console.error("There was an error fetching the courses!", error);
    }
  };

  useEffect(() => {
    fetchCourseInstances();
  }, [filterYear, filterSemester]);

  useEffect(() => {
    fetchCourses(); // Fetch courses when component mounts
  }, []);

  // Filter instances based on year and semester
  useEffect(() => {
    setFilteredInstances(courseInstances);
  }, [courseInstances]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewInstance((prev) => ({
      ...prev,
      [name]: value
    }));
  };

  const handleAddInstance = async () => {
    try {
      const response = await axios.post('http://127.0.0.1:8000/api/instances/', newInstance);
      setCourseInstances((prev) => [...prev, response.data]);
      setFilteredInstances((prev) => [...prev, response.data]);
      alert("Course instance added successfully.");
      // Reset form
      setNewInstance({
        course: '',
        year: '',
        semester: ''
      });
    } catch (error) {
      console.error("There was an error adding the course instance!", error);
    }
  };

  return (
    <div>
      <h2>Course Instances</h2>

      <div className="filters">
        <input
          type="text"
          placeholder="Filter by year"
          value={filterYear}
          onChange={e => setFilterYear(e.target.value)}
        />
        <input
          type="text"
          placeholder="Filter by semester"
          value={filterSemester}
          onChange={e => setFilterSemester(e.target.value)}
        />
        <button onClick={fetchCourseInstances}>Apply Filters</button>
      </div>

      <div className="add-instance-form mt-4">
        <h3>Add New Course Instance</h3>
        <select
          name="course"
          value={newInstance.course}
          onChange={handleInputChange}
        >
          <option value="">Select Course</option>
          {courses.map(course => (
            <option key={course.id} value={course.id}>
              {course.title}
            </option>
          ))}
        </select>
        <input
          type="text"
          name="year"
          placeholder="Enter year"
          value={newInstance.year}
          onChange={handleInputChange}
        />
        <input
          type="text"
          name="semester"
          placeholder="Enter semester"
          value={newInstance.semester}
          onChange={handleInputChange}
        />
        <button onClick={handleAddInstance}>Add Instance</button>
      </div>

      <table className="table-auto w-full mt-4 border border-gray-300">
        <thead>
          <tr className="bg-gray-100">
            <th className="border px-4 py-2">Course Title</th>
            <th className="border px-4 py-2">Year</th>
            <th className="border px-4 py-2">Semester</th>
          </tr>
        </thead>
        <tbody>
          {filteredInstances.map((instance, index) => (
            <tr key={index}>
              <td className="border px-4 py-2">{instance.course.title}</td>
              <td className="border px-4 py-2">{instance.year}</td>
              <td className="border px-4 py-2">{instance.semester}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default CourseInstanceList;
