import React, { useState, useEffect } from 'react';
import axios from 'axios';

const CourseInstanceList = () => {
  const [courseInstances, setCourseInstances] = useState([]);
  const [filteredInstances, setFilteredInstances] = useState([]);
  const [filterYear, setFilterYear] = useState('');
  const [filterSemester, setFilterSemester] = useState('');

  // Fetch course instances when the component mounts
  useEffect(() => {
    axios.get('http://127.0.0.1:8000/api/instances/')
      .then(response => {
        setCourseInstances(response.data);
        setFilteredInstances(response.data); // Set initial filtered instances to all instances
      })
      .catch(error => console.error("There was an error fetching the course instances!", error));
  }, []);

  // Function to filter instances based on year and semester
  const filterInstances = () => {
    let filtered = courseInstances;

    if (filterYear) {
      filtered = filtered.filter(instance => instance.year === filterYear);
    }

    if (filterSemester) {
      filtered = filtered.filter(instance => instance.semester.toLowerCase() === filterSemester.toLowerCase());
    }

    setFilteredInstances(filtered);
  };

  // Filter instances whenever filter inputs change
  useEffect(() => {
    filterInstances();
  }, [filterYear, filterSemester]);

  return (
    <div>
      <h2>Course Instances</h2>

      <div className="filters">
        <input
          type="text"
          placeholder="Filter by year"
          value={filterYear}
          onChange={e => setFilterYear(e.target.value)}
        />
        <input
          type="text"
          placeholder="Filter by semester"
          value={filterSemester}
          onChange={e => setFilterSemester(e.target.value)}
        />
      </div>

      <table className="table-auto w-full mt-4 border border-gray-300">
        <thead>
          <tr className="bg-gray-100">
            <th className="border px-4 py-2">Course Title</th>
            <th className="border px-4 py-2">Year</th>
            <th className="border px-4 py-2">Semester</th>
          </tr>
        </thead>
        <tbody>
          {filteredInstances.map((instance, index) => (
            <tr key={index}>
              <td className="border px-4 py-2">{instance.course.title}</td>
              <td className="border px-4 py-2">{instance.year}</td>
              <td className="border px-4 py-2">{instance.semester}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default CourseInstanceList;
