import React, { useSyncExternalStore } from 'react'
import { useState } from 'react';
import axios from 'axios';
import { useEffect } from 'react';
const AddCourseInstance = () => {
  const [selectedCourse, setSelectedCourse] = useState('');
  const [courses, setCourses] = useState([]);
  const [semester, setSemester] = useState('');
  const [year, setYear] = useState('');

  // Fetch courses when the component mounts
  useEffect(() => {
    axios.get('http://127.0.0.1:8000/api/courses/')
      .then(response => {
        setCourses(response.data);
        setSelectedCourse(response.data.length > 0 ? response.data[0].id : ''); // Default to first course
      })
      .catch(error => console.error("There was an error fetching the courses!", error));
  }, []);

  // Function to handle adding a course instance
  const addInstance = async () => {
    const instanceData = {
      course: selectedCourse,
      year,
      semester,
    };

    try {
      const response = await axios.post('http://127.0.0.1:8000/api/instances/', instanceData);
      console.log("Instance added successfully:", response.data);
      // Optionally, reset the form fields
      setSemester('');
      setYear('');
    } catch (error) {
      console.error("There was an error adding the course instance:", error);
    }
  };

  return (
    <div>
      <h2>Add Course Instance</h2>
      <div>
        <label>Select Course:</label>
        <select value={selectedCourse} onChange={e => setSelectedCourse(e.target.value)}>
          {courses.map(course => (
            <option key={course.id} value={course.id}>
              {course.title}
            </option>
          ))}
        </select>
      </div>
      <div>
        <label>Semester:</label>
        <input
          type="text"
          placeholder="Enter semester"
          value={semester}
          onChange={e => setSemester(e.target.value)}
        />
      </div>
      <div>
        <label>Year:</label>
        <input
          type="text"
          placeholder="Enter year"
          value={year}
          onChange={e => setYear(e.target.value)}
        />
      </div>
      <button onClick={addInstance}>Add Instance</button>
    </div>
  );
}

export default AddCourseInstance
