// src/App.js
import React, { useState } from 'react';
import CourseList from './components/CourseList';
import CourseForm from './components/CourseForm';
import CourseDetail from './components/CourseDetail';
import CourseInstanceForm from './components/CourseInstanceForm';
import CourseInstanceComponent from './components/CourseInstanceComponent';
import AddCourseInstance from './components/AddCourseInstance';

const App = () => {
  const [selectedCourse, setSelectedCourse] = useState(null);

  const refreshCourses = () => {
    setSelectedCourse(null);
  };

  return (
    <div>
      <h1>Course Management</h1>
      <CourseForm selectedCourse={selectedCourse} refreshCourses={refreshCourses} />
      <CourseList selectCourse={setSelectedCourse} />
      <CourseDetail course={selectedCourse} />
      <AddCourseInstance/>  
    </div>
  );
};

export default App;
