import React, { useSyncExternalStore } from 'react'
import { useState } from 'react';
import axios from 'axios';
import { useEffect } from 'react';
const AddCourseInstance = () => {
  const [selectedCourse, setSelectedCourse] = useState('');
  const[courses,setCourses]=useState([])  
const [sem,setsem]=useState()
const [year,setyear]=useState()
    useEffect(() => {
      axios.get('http://127.0.0.1:8000/api/courses/')
        .then(response => {
          setCourses(response.data);
        })
        .catch(error => console.error("There was an error fetching the courses!", error));
    },[]);

  return (      
    <div>AddCourseInstance
      <div>
      <label>Select Course:</label>
      <select value={courses}></select>
      <input type="text" placeholder="enter semester" onChange={e=>setsem(e.target.value)}></input>
      <input type="text" value={year} placeholder="enter year" onChange={e=>setyear(e.target.value)}></input>
      <button>Add instance</button>
      </div>

    </div>
  )
}

export default AddCourseInstance
