import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const CourseList = () => {
  const [courses, setCourses] = useState([]);

  useEffect(() => {
    axios.get('http://127.0.0.1:8000/api/courses/')
      .then(response => setCourses(response.data))
      .catch(error => console.error('Error fetching courses:', error));
  }, []);

  const handleDelete = (id) => {
    axios.delete(`http://127.0.0.1:8000/api/courses/${id}/`)
      .then(() => setCourses(courses.filter(course => course.id !== id)))
      .catch(error => console.error('Error deleting course:', error));
  };

  return (
    <div className="p-4 border rounded-md shadow-md">
      <h2 className="text-lg font-semibold mb-4">Courses</h2>
      <ul>
        {courses.map(course => (
          <li key={course.id} className="border p-2 mb-2 rounded-md">
            <div className="flex justify-between">
              <span>{course.title} - {course.code}</span>
              <div>
                <Link to={`/courses/${course.id}`} className="text-blue-500 mr-2">View</Link>
                <button onClick={() => handleDelete(course.id)} className="text-red-500">Delete</button>
              </div>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default CourseList;
