// src/components/CourseList.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const CourseList = ({ selectCourse }) => {
  const [courses, setCourses] = useState([]);

  useEffect(() => {
    axios.get('http://127.0.0.1:8000/api/courses/')
      .then(response => {
        setCourses(response.data);
      })
      .catch(error => console.error("There was an error fetching the courses!", error));
  }, []);

  const deleteCourse = (id) => {
    axios.delete(`http://127.0.0.1:8000/api/courses/${id}/`)
      .then(() => {
        setCourses(courses.filter(course => course.id !== id));
      })
      .catch(error => console.error("There was an error deleting the course!", error));
  };

  return (
    <div>
      <h2 className='font-bold'>Courses</h2>
      <ul>
        {courses.map(course => (
          <li key={course.id}>
            {course.title} 
            <button onClick={() => selectCourse(course)}>View</button>
            <button onClick={() => deleteCourse(course.id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default CourseList;
