import React, { useState } from 'react';
import axios from 'axios';

const CourseInstanceForm = () => {
  const [year, setYear] = useState('');
  const [semester, setSemester] = useState('');
  const [courseId, setCourseId] = useState('');
  const [instance, setInstance] = useState({});

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await axios.post('/instances/', {
        year,
        semester,
        course_id: courseId,
      });
      setInstance(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Year:
        <input type="number" value={year} onChange={(event) => setYear(event.target.value)} />
      </label>
      <label>
        Semester:
        <input type="number" value={semester} onChange={(event) => setSemester(event.target.value)} />
      </label>
      <label>
        Course ID:
        <input type="number" value={courseId} onChange={(event) => setCourseId(event.target.value)} />
      </label>
      <button type="submit">Add Instance</button>
      {instance && (
        <div>
          <h2>Instance Detail</h2>
          <p>Year: {instance.year}</p>
          <p>Semester: {instance.semester}</p>
          <p>Course ID: {instance.course_id}</p>
        </div>
      )}
    </form>
  );
};

export default CourseInstanceForm;
