// src/App.js
import React, { useState } from 'react';
import CourseList from './components/CourseList';
import CourseForm from './components/CourseForm';
import CourseDetail from './components/CourseDetail';

const App = () => {
  const [selectedCourse, setSelectedCourse] = useState(null);

  const refreshCourses = () => {
    setSelectedCourse(null);
  };

  return (
    <div className=' flex w-full flex-row justify-between p-2'> 

      <div className=' w-full flex flex-row gap-20 justify-evenly'>
      <CourseForm selectedCourse={selectedCourse} refreshCourses={refreshCourses} />
      <CourseList selectCourse={setSelectedCourse} />
      </div>
    </div>
  );
};

export default App;
